SELECT tb_prod.prod_idx AS prod_idx, tb_prod.prod_name AS prod_name, tb_prod.prod_desc AS prod_desc, tb_prod.prod_type AS prod_type, tb_prod.prod_price AS prod_price, tb_prod.prod_stock AS prod_stock, tb_prod.cust_id AS prod_cust_id, tb_prod.created_at AS prod_created_at, tb_cust.cust_name, tb_cust.cust_nick, tb_cust.cust_phone, tb_cust.cust_addr, tb_review.prod_ratings AS prod_ratings FROM tb_prod JOIN tb_cust ON tb_prod.cust_id = tb_cust.cust_id LEFT JOIN tb_review ON tb_prod.prod_idx = tb_review.prod_idx WHERE tb_prod.prod_name LIKE CONCAT('%', #{searchInput}, '%') ORDER BY tb_prod.created_at DESC;
